package lk.solution.health.excellent.transaction.dao;import lk.solution.health.excellent.resource.entity.Patient;import lk.solution.health.excellent.resource.entity.User;import lk.solution.health.excellent.transaction.entity.Invoice;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.List;@Repositorypublic interface InvoiceDao extends JpaRepository<Invoice, Integer> {    Invoice findFirstByOrderByIdDesc();    List<Invoice> findByPatient(Patient patient);    Invoice findByPatientAndCreatedAt(Patient patient, LocalDate createdAt);    List<Invoice> findByCreatedAtAndUser(LocalDate createdAt, User user);    List<Invoice> findByCreatedAt(LocalDate createdAt);    int countByCreatedAt(LocalDate createdAt);    List<Invoice> findByCreatedAtIsBetween(LocalDate createdAt, LocalDate createdAt2);    Integer countByCreatedAtIsBetween(LocalDate createdAt, LocalDate createdAt2);    Invoice findByNumber(int number);    int countByCreatedAtAndUser(LocalDate toDay, User user);}