package lk.solution.health.excellent.transaction.dao;import lk.solution.health.excellent.resource.entity.Patient;import lk.solution.health.excellent.resource.entity.User;import lk.solution.health.excellent.transaction.entity.Invoice;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.List;@Repositorypublic interface InvoiceDao extends JpaRepository<Invoice, Integer> {    Invoice findFirstByOrderByIdDesc();    List<Invoice> findByPatient(Patient patient);    Invoice findByPatientAndCreatedAt(Patient patient, LocalDateTime createdAt);    List<Invoice> findByCreatedAtAndUser(LocalDateTime createdAt, User user);    List<Invoice> findByCreatedAt(LocalDateTime createdAt);    Integer countByCreatedAt(LocalDateTime createdAt);    List<Invoice> findByCreatedAtIsBetween(LocalDateTime createdAt, LocalDateTime createdAt2);    Integer countByCreatedAtIsBetween(LocalDateTime createdAt, LocalDateTime createdAt2);}